import styled from 'styled-components'
import Image from 'next/image'

export const Container = styled.ul`
	margin-top: 20px;

	li {
		display: flex;
		justify-content: space-between;
		align-items: center;
		color: #444;

		& + li {
			margin-top: 15px;
		}
	}
`

export const FileInfo = styled.div`
	display: flex;
	align-items: center;

	div {
		display: flex;
		flex-direction: column;
		margin-left: 10px;

		span {
			font-size: 12px;
			color: #999;
			margin-top: 5px;

			button {
				border: 0;
				background: transparent;
				color: #e57878;
				margin-left: 5px;
				cursor: pointer;
			}
		}
	}
`

export const ImagePreview = styled(Image)`
	border-radius: 50%;
`
